mapper.select:
select C.cmntId, C.postId, C.memberId, C.content, C.createdDate
from Cmnt C
where C.cmntId = #{cmntId}

mapper.insert:
insert into Cmnt
(
    cmntId,
    postId,
    memberId,
    content,
    createdDate
)
values
    (
    CMNT_SEQ.NEXTVAL
    ,#{postId}
    ,#{memberId}
    ,#{content}
    ,sysdate
    )

mapper.update:
UPDATE Cmnt
SET content = #{content}
WHERE cmntId = #{cmntId}
